package hainu.com.adultloan.activity;import android.content.Intent;import android.net.Uri;import android.os.Bundle;import android.view.View;import hainu.com.adultloan.R;public class AboutAsActivity extends BaseActivity {    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_about_as);    }    public void back(View view){        finish();    }    public void openCompanyNet(View view) {        Intent intent = new Intent();        intent.setAction("android.intent.action.VIEW");        Uri content_url = Uri.parse("http://www.58daxuesheng.com/");        intent.setData(content_url);        startActivity(intent);    }}